package com.masamiaoi.sqlite.util;import org.springframework.boot.jdbc.DataSourceBuilder;import org.springframework.util.StringUtils;import org.sqlite.JDBC;import javax.sql.DataSource;/** * @author maamiaoi */public class SqliteBuilder {    private String filePath;    private String url;    public static SqliteBuilder create() {        return new SqliteBuilder();    }    public SqliteBuilder filePath(String filePath) {        this.filePath = filePath;        return this;    }    public SqliteBuilder url(String url) {        this.url = url;        return this;    }    public DataSource build() {        if (StringUtils.hasText(url)) {            return DataSourceBuilder.create().                    url(url).driverClassName(JDBC.class.getName()).build();        }        if (StringUtils.hasText(filePath)) {            String stringBuilder = "jdbc:sqlite:" + filePath;            return DataSourceBuilder.create().                    url(stringBuilder).driverClassName(JDBC.class.getName()).build();        }        return DataSourceBuilder.create().build();    }}